import http from '@/http-common'

/**
 * Product Data Service
 */
class ProductDataService {
  getAll () {
    return http.get('/product')
  }

  create (data) {
    return http.post('/product', data)
  }

  get (data) {
    return http.get(`/product/${id}`)
  }
}

